import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage-mongo";
import { GitAnalyzer } from "./services/git-analyzer";
import { AIAnalyzer } from "./services/ai-analyzer";
import { insertRepositorySchema, insertQuerySchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  const gitAnalyzer = new GitAnalyzer();
  const aiAnalyzer = new AIAnalyzer();

  // Get all repositories
  app.get("/api/repositories", async (req, res) => {
    try {
      const repositories = await storage.getAllRepositories();
      res.json(repositories);
    } catch (error) {
      res.status(500).json({ error: (error as Error).message });
    }
  });

  // Get repository by ID
  app.get("/api/repositories/:id", async (req, res) => {
    try {
      const repository = await storage.getRepository(req.params.id);
      if (!repository) {
        return res.status(404).json({ error: "Repository not found" });
      }
      res.json(repository);
    } catch (error) {
      res.status(500).json({ error: (error as Error).message });
    }
  });

  // Helper function to normalize GitHub URLs
  function normalizeGitHubUrl(url: string): string {
    try {
      const urlObj = new URL(url);

      // Handle GitHub URLs
      if (urlObj.hostname === 'github.com') {
        const pathParts = urlObj.pathname.split('/').filter(Boolean);
        if (pathParts.length >= 2) {
          const owner = pathParts[0];
          let repo = pathParts[1];
          // Remove .git suffix if it already exists to avoid duplication
          if (repo.endsWith('.git')) {
            repo = repo.slice(0, -4);
          }
          return `https://github.com/${owner}/${repo}.git`;
        }
      }

      // Return original URL if not GitHub or can't parse
      return url;
    } catch {
      return url;
    }
  }

  // Create and analyze repository
  app.post("/api/repositories", async (req, res) => {
    try {
      const validatedData = insertRepositorySchema.parse(req.body);

      // Normalize the URL
      const normalizedUrl = normalizeGitHubUrl(validatedData.url);
      const repositoryData = { ...validatedData, url: normalizedUrl };

      // Check if repository already exists, if so return existing one
      const existing = await storage.getRepositoryByUrl(normalizedUrl);
      let repository;

      if (existing) {
        // Reset analysis status to queued for re-analysis  
        const existingId = existing._id || existing.id;
        repository = await storage.updateRepository(existingId, { analysisStatus: "queued" });
      } else {
        // Create new repository record
        repository = await storage.createRepository(repositoryData);
      }
      res.json(repository);

      // Start analysis in background
      const repositoryId = repository._id || repository.id;
      analyzeRepositoryBackground(repositoryId);
    } catch (error) {
      res.status(400).json({ error: (error as Error).message });
    }
  });

  // Get repository analysis progress
  app.get("/api/repositories/:id/progress", async (req, res) => {
    try {
      const repository = await storage.getRepository(req.params.id);
      if (!repository) {
        return res.status(404).json({ error: "Repository not found" });
      }

      const commits = await storage.getCommitsByRepository(req.params.id);
      const changeEvents = await storage.getChangeEventsByRepository(req.params.id);

      res.json({
        status: repository.analysisStatus,
        commitCount: commits.length,
        changeEventCount: changeEvents.length,
        progress: getAnalysisProgress(repository.analysisStatus, commits.length),
      });
    } catch (error) {
      res.status(500).json({ error: (error as Error).message });
    }
  });

  // Submit query
  app.post("/api/repositories/:id/query", async (req, res) => {
    try {
      const validatedData = insertQuerySchema.parse({
        ...req.body,
        repositoryId: req.params.id,
      });

      const repository = await storage.getRepository(req.params.id);
      if (!repository) {
        return res.status(404).json({ error: "Repository not found" });
      }

      if (repository.analysisStatus !== "completed") {
        return res.status(400).json({ error: "Repository analysis not completed" });
      }

      // Get AI answer
      const queryResponse = await aiAnalyzer.answerQuery(req.params.id, validatedData.question);

      // Store query and response
      const query = await storage.createQuery({
        ...validatedData,
        answer: queryResponse.answer,
        relatedCommits: queryResponse.relatedCommits,
        relatedEvents: queryResponse.relatedEvents,
      });

      res.json({
        ...query,
        confidence: queryResponse.confidence,
      });
    } catch (error) {
      res.status(500).json({ error: (error as Error).message });
    }
  });

  // Get repository queries
  app.get("/api/repositories/:id/queries", async (req, res) => {
    try {
      const queries = await storage.getQueriesByRepository(req.params.id);
      res.json(queries);
    } catch (error) {
      res.status(500).json({ error: (error as Error).message });
    }
  });

  // Get repository change events
  app.get("/api/repositories/:id/events", async (req, res) => {
    try {
      const events = await storage.getChangeEventsByRepository(req.params.id);
      res.json(events);
    } catch (error) {
      res.status(500).json({ error: (error as Error).message });
    }
  });

  // Get repository commits
  app.get("/api/repositories/:id/commits", async (req, res) => {
    try {
      const commits = await storage.getCommitsByRepository(req.params.id);
      res.json(commits);
    } catch (error) {
      res.status(500).json({ error: (error as Error).message });
    }
  });

  // Retry failed repository analysis
  app.post("/api/repositories/:id/retry", async (req, res) => {
    try {
      const repository = await storage.getRepository(req.params.id);
      if (!repository) {
        return res.status(404).json({ error: "Repository not found" });
      }

      // Reset status to queued
      await storage.updateRepository(req.params.id, { analysisStatus: "queued" });

      // Start analysis in background
      analyzeRepositoryBackground(req.params.id);

      res.json({ message: "Analysis restarted" });
    } catch (error) {
      res.status(500).json({ error: error.message });
    }
  });

  // Background analysis function
  async function analyzeRepositoryBackground(repositoryId: string) {
    try {
      const repository = await storage.getRepository(repositoryId);
      if (!repository) {
        console.error(`Repository not found: ${repositoryId}`);
        return;
      }

      console.log(`Starting analysis for repository:`, JSON.stringify(repository, null, 2));

      // Update status to cloning
      await storage.updateRepository(repositoryId, { analysisStatus: "cloning" });

      // Clone repository
      await gitAnalyzer.cloneRepository(repository);

      // Update status to analyzing
      await storage.updateRepository(repositoryId, { analysisStatus: "analyzing" });

      // Analyze commits
      const commits = await gitAnalyzer.analyzeCommits(repositoryId);
      console.log(`Analyzed ${commits.length} commits for repository ${repositoryId}`);

      // Get repository stats
      const stats = await gitAnalyzer.getRepositoryStats();
      console.log(`Repository stats: ${stats.contributors.size} contributors, ${stats.totalFiles} files`);

      // Analyze with AI
      console.log(`About to retrieve commits for repository: ${repositoryId}`);
      const dbCommits = await storage.getCommitsByRepository(repositoryId);
      console.log(`Retrieved ${dbCommits.length} commits from database`);
      await aiAnalyzer.analyzeCommitBatches(repositoryId, dbCommits);

      // Get final change events count
      const changeEvents = await storage.getChangeEventsByRepository(repositoryId);
      const majorFeatures = changeEvents.filter(e => e.category === 'new_feature');

      // Update repository with final stats
      const updateData = {
        analysisStatus: "completed" as const,
        lastAnalyzedAt: new Date(),
        commitCount: dbCommits.length,
        contributorCount: stats.contributors.size,
        fileCount: stats.totalFiles,
        changeEventCount: changeEvents.length,
        majorFeatureCount: majorFeatures.length,
        confidenceScore: 94, // Default confidence score
      };

      console.log(`Updating repository with stats:`, updateData);
      await storage.updateRepository(repositoryId, updateData);

      // Cleanup
      await gitAnalyzer.cleanup();

      console.log(`Analysis completed for repository ${repositoryId}`);
    } catch (error) {
      console.error(`Analysis failed for repository ${repositoryId}:`, (error as Error).message);
      console.error(`Error stack:`, (error as Error).stack);

      // Get repository info for debugging
      try {
        const repository = await storage.getRepository(repositoryId);
        console.error(`Failed repository data:`, JSON.stringify(repository, null, 2));
      } catch (dbError) {
        console.error(`Could not retrieve repository data:`, (dbError as Error).message);
      }

      await storage.updateRepository(repositoryId, { analysisStatus: "failed" });
      await gitAnalyzer.cleanup();
    }
  }

  function getAnalysisProgress(status: string, commitCount: number): number {
    switch (status) {
      case "queued": return 0;
      case "cloning": return 25;
      case "analyzing": return 50 + Math.min(45, (commitCount / 1000) * 45);
      case "completed": return 100;
      case "failed": return 0;
      default: return 0;
    }
  }

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type Repository, 
  type InsertRepository, 
  type Commit, 
  type ChangeEvent, 
  type Query, 
  type InsertQuery 
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Repository operations
  createRepository(repo: InsertRepositoryType): Promise<Repository>;
  getRepository(id: string): Promise<Repository | undefined>;
  getRepositoryByUrl(url: string): Promise<Repository | undefined>;
  updateRepository(id: string, updates: Partial<Repository>): Promise<Repository>;
  getAllRepositories(): Promise<Repository[]>;

  // Commit operations
  createCommits(commits: Omit<Commit, '_id'>[]): Promise<void>;
  getCommitsByRepository(repositoryId: string): Promise<Commit[]>;
  getCommitsByShas(repositoryId: string, shas: string[]): Promise<Commit[]>;

  // Change event operations
  createChangeEvents(events: Omit<ChangeEvent, '_id'>[]): Promise<void>;
  getChangeEventsByRepository(repositoryId: string): Promise<ChangeEvent[]>;
  getChangeEventsByIds(ids: string[]): Promise<ChangeEvent[]>;

  // Query operations
  createQuery(query: InsertQueryType & { answer: string; relatedCommits: string[]; relatedEvents: string[] }): Promise<Query>;
  getQueriesByRepository(repositoryId: string): Promise<Query[]>;
}

export class MemStorage implements IStorage {
  private repositories: Map<string, Repository> = new Map();
  private commits: Map<string, Commit> = new Map();
  private changeEvents: Map<string, ChangeEvent> = new Map();
  private queries: Map<string, Query> = new Map();

  async createRepository(insertRepo: InsertRepositoryType): Promise<Repository> {
    const id = randomUUID();
    const repo: Repository = {
      _id: id,
      ...insertRepo,
      analysisStatus: "queued",
      createdAt: new Date(),
      lastAnalyzedAt: undefined,
      commitCount: 0,
      contributorCount: 0,
      fileCount: 0,
      changeEventCount: 0,
      majorFeatureCount: 0,
      confidenceScore: 0,
    };
    this.repositories.set(id, repo);
    return repo;
  }

  async getRepository(id: string): Promise<Repository | undefined> {
    return this.repositories.get(id);
  }

  async getRepositoryByUrl(url: string): Promise<Repository | undefined> {
    return Array.from(this.repositories.values()).find(repo => repo.url === url);
  }

  async updateRepository(id: string, updates: Partial<Repository>): Promise<Repository> {
    const existing = this.repositories.get(id);
    if (!existing) throw new Error(`Repository ${id} not found`);

    const updated = { ...existing, ...updates };
    this.repositories.set(id, updated);
    return updated;
  }

  async getAllRepositories(): Promise<Repository[]> {
    return Array.from(this.repositories.values());
  }

  async createCommits(commits: Omit<Commit, '_id'>[]): Promise<void> {
    console.log(`MemStorage.createCommits called with ${commits.length} commits`);
    for (const commit of commits) {
      const id = randomUUID();
      this.commits.set(id, { _id: id, ...commit });
      console.log(`Saved commit ${commit.sha?.substring(0,8)} with id ${id}`);
    }
    console.log(`MemStorage now has ${this.commits.size} total commits`);
  }

  async getCommitsByRepository(repositoryId: string): Promise<Commit[]> {
    console.log(`MemStorage.getCommitsByRepository called for ${repositoryId}`);
    console.log(`Total commits in storage: ${this.commits.size}`);
    const allCommits = Array.from(this.commits.values());
    console.log(`All repository IDs in storage:`, allCommits.map(c => c.repositoryId));
    const filtered = allCommits.filter(c => c.repositoryId === repositoryId);
    console.log(`Found ${filtered.length} commits for repository ${repositoryId}`);
    return filtered;
  }

  async getCommitsByShas(repositoryId: string, shas: string[]): Promise<Commit[]> {
    return Array.from(this.commits.values()).filter(
      c => c.repositoryId === repositoryId && shas.includes(c.sha)
    );
  }

  async createChangeEvents(events: Omit<ChangeEvent, '_id'>[]): Promise<void> {
    for (const event of events) {
      const id = randomUUID();
      this.changeEvents.set(id, { _id: id, ...event });
    }
  }

  async getChangeEventsByRepository(repositoryId: string): Promise<ChangeEvent[]> {
    return Array.from(this.changeEvents.values()).filter(e => e.repositoryId === repositoryId);
  }

  async getChangeEventsByIds(ids: string[]): Promise<ChangeEvent[]> {
    return Array.from(this.changeEvents.values()).filter(e => ids.includes(e.id));
  }

  async createQuery(queryData: InsertQuery & { answer: string; relatedCommits: string[]; relatedEvents: string[] }): Promise<Query> {
    const id = randomUUID();
    const query: Query = {
      id,
      ...queryData,
      createdAt: new Date(),
    };
    this.queries.set(id, query);
    return query;
  }

  async getQueriesByRepository(repositoryId: string): Promise<Query[]> {
    return Array.from(this.queries.values()).filter(q => q.repositoryId === repositoryId);
  }
}

export const storage = new MemStorage();

