import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { CheckCircle, Clock, Settings, AlertCircle } from "lucide-react";
import type { Repository } from "@shared/schema";
import type { AnalysisProgress } from "@/lib/types";

interface AnalysisProgressProps {
  repository: Repository;
  progress: AnalysisProgress;
}

export default function AnalysisProgress({ repository, progress }: AnalysisProgressProps) {
  const getStepStatus = (step: string) => {
    switch (progress.status) {
      case "completed":
        return "completed";
      case "failed":
        return step === "cloning" ? "failed" : "pending";
      case "cloning":
        return step === "cloning" ? "active" : step === "extracting" || step === "analyzing" || step === "indexing" ? "pending" : "completed";
      case "analyzing":
        return step === "analyzing" ? "active" : step === "indexing" ? "pending" : "completed";
      default:
        return "pending";
    }
  };

  const steps = [
    {
      id: "cloning",
      title: "Repository Cloning",
      description: `Cloned repository with ${progress.commitCount} commits`,
      icon: CheckCircle,
    },
    {
      id: "extracting",
      title: "Change Extraction",
      description: "Extracted diffs and metadata from all commits",
      icon: CheckCircle,
    },
    {
      id: "analyzing",
      title: "AI Semantic Analysis",
      description: `Categorizing changes and extracting rationale... (${progress.changeEventCount} events identified)`,
      icon: Settings,
    },
    {
      id: "indexing",
      title: "Building Temporal Index",
      description: "Creating searchable embeddings for Q&A",
      icon: Clock,
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50/30 via-purple-50/20 to-indigo-50/30">
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_top_right,rgba(59,130,246,0.1),transparent_50%)]" />

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-16 relative">
        <Card className="glass-card backdrop-blur-xl border-white/20 shadow-2xl">
          <CardContent className="p-10">
            <div className="text-center mb-10">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-500 to-purple-600 rounded-3xl flex items-center justify-center mx-auto mb-6 shadow-lg floating-animation">
                <Settings className="text-white w-10 h-10 animate-spin" />
              </div>
              <h2 className="text-3xl font-bold gradient-text mb-4">Analyzing Repository</h2>
              <p className="text-slate-600 text-lg mb-6 max-w-2xl mx-auto" data-testid="text-repository-url">
                {repository.url}
              </p>
              <div className="flex items-center justify-center space-x-6">
                <div className="text-center">
                  <div className="text-4xl font-bold gradient-text" data-testid="text-progress-percentage">
                    {Math.round(progress.progress)}%
                  </div>
                  <div className="text-sm text-slate-500 font-medium">Complete</div>
                </div>
              </div>
            </div>

            {/* Enhanced Progress Bar */}
            <div className="mb-10">
              <div className="flex items-center justify-between mb-3">
                <span className="text-sm font-medium text-slate-700">Overall Progress</span>
                <span className="text-sm font-medium text-slate-700">{Math.round(progress.progress)}%</span>
              </div>
              <div className="w-full bg-white/50 rounded-full h-3 shadow-inner backdrop-blur-sm">
                <div 
                  className="bg-gradient-to-r from-blue-500 to-purple-600 h-3 rounded-full transition-all duration-500 shadow-sm"
                  style={{ width: `${progress.progress}%` }}
                />
              </div>
            </div>

            {/* Enhanced Steps */}
            <div className="space-y-6">
              {steps.map((step, index) => {
                const status = getStepStatus(step.id);
              const Icon = step.icon;

                const stepColors = {
                  completed: "bg-green-500 text-white",
                  active: "bg-blue-500 text-white",
                  failed: "bg-red-500 text-white",
                  pending: "bg-gray-200 text-gray-500"
                };

                return (
                  <div key={step.id} className="relative group" data-testid={`progress-step-${step.id}`}>
                    <div className="flex items-start space-x-6 p-6 bg-white/50 backdrop-blur-sm rounded-xl border border-white/30 hover:bg-white/70 transition-all duration-300">
                      {/* Step Icon with Connection Line */}
                      <div className="flex flex-col items-center">
                        <div className={`w-12 h-12 rounded-xl flex items-center justify-center shadow-lg ${stepColors[status]} transition-all duration-300 group-hover:scale-110`}>
                          {status === "active" ? (
                            <Settings className="w-6 h-6 animate-spin" />
                          ) : status === "failed" ? (
                            <AlertCircle className="w-6 h-6" />
                          ) : (
                            <step.icon className="w-6 h-6" />
                          )}
                        </div>
                        {index < steps.length - 1 && (
                          <div className={`w-1 h-12 mt-4 rounded-full transition-all duration-500 ${
                            status === "completed" ? "bg-green-300" : "bg-gray-200"
                          }`} />
                        )}
                      </div>

                      {/* Step Content */}
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between mb-3">
                          <h4 className="text-lg font-semibold text-slate-800">{step.title}</h4>
                          <div className={`px-3 py-1 rounded-full text-xs font-medium ${
                            status === "completed" ? "bg-green-100 text-green-700" :
                            status === "active" ? "bg-blue-100 text-blue-700" :
                            status === "failed" ? "bg-red-100 text-red-700" :
                            "bg-gray-100 text-gray-600"
                          }`}>
                            {status === "completed" ? "Completed" :
                             status === "active" ? "In Progress" :
                             status === "failed" ? "Failed" : "Pending"}
                          </div>
                        </div>
                        <p className="text-slate-600 leading-relaxed">{step.description}</p>
                        {status === "active" && step.id === "analyzing" && (
                          <div className="mt-4">
                            <div className="w-full bg-white/50 rounded-full h-2">
                              <div 
                                className="bg-gradient-to-r from-blue-500 to-purple-600 h-2 rounded-full transition-all duration-500"
                                style={{ width: `${progress.progress}%` }}
                              />
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                );
            })}
            </div>

            <div className="mt-10 pt-8 border-t border-white/20">
              <div className="text-center">
                <div className="flex items-center justify-center space-x-2 text-slate-600">
                  <div className="w-2 h-2 bg-gradient-to-r from-blue-400 to-purple-500 rounded-full animate-pulse"></div>
                  <span className="text-sm font-medium">
                    {progress.status === "completed" 
                      ? "Analysis completed successfully!" 
                      : progress.status === "failed"
                      ? "Analysis failed - please try again"
                      : "Estimated time remaining: 3-5 minutes"}
                  </span>
                </div>
                {repository.createdAt && (
                  <div className="mt-2 text-xs text-slate-500" data-testid="text-analysis-time">
                    Started {new Date(repository.createdAt).toLocaleTimeString()}
                  </div>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/query-interface.ts.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest, getQueryFn } from "@/lib/queryClient";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Send, Search, Info } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import type { QueryResult } from "@/lib/types";
import type { Query } from "@shared/schema";

interface QueryInterfaceProps {
  repositoryId: string;
}

const suggestedQueries = [
  '"Why was Redux introduced?"',
  '"Show major architecture changes"',
  '"How did testing evolve?"',
  '"What authentication patterns were added?"',
];

export default function QueryInterface({ repositoryId }: QueryInterfaceProps) {
  const [question, setQuestion] = useState("");
  const [currentResult, setCurrentResult] = useState<QueryResult | null>(null);
  const queryClient = useQueryClient();

  const submitQueryMutation = useMutation({
    mutationFn: async (question: string) => {
      const response = await apiRequest("POST", `/api/repositories/${repositoryId}/query`, {
        question,
      });
      return response.json() as Promise<Query & { confidence?: number }>;
    },
    onSuccess: (data) => {
      setCurrentResult({
        id: data.id,
        question: data.question,
        answer: data.answer,
        relatedCommits: data.relatedCommits,
        relatedEvents: data.relatedEvents,
        createdAt: data.createdAt.toString(),
        confidence: data.confidence,
      });
      setQuestion("");
      // Invalidate queries to refresh recent queries
      queryClient.invalidateQueries({ queryKey: ["/api/repositories", repositoryId, "queries"] });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (question.trim()) {
      submitQueryMutation.mutate(question.trim());
    }
  };

  const handleSuggestedQuery = (suggested: string) => {
    setQuestion(suggested.replace(/"/g, ''));
  };

  return (
    <div className="space-y-6">
      {/* Query Input */}
      <Card className="shadow-sm">
        <CardContent className="p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Ask About Your Code Evolution</h3>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="relative">
              <Textarea
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Ask anything... e.g., 'Why was authentication added?' or 'How did the component architecture evolve?'"
                rows={3}
                className="resize-none pr-16"
                data-testid="textarea-query-input"
              />
              <Button
                type="submit"
                size="sm"
                className="absolute bottom-3 right-3 bg-github-blue hover:bg-blue-700"
                disabled={!question.trim() || submitQueryMutation.isPending}
                data-testid="button-submit-query"
              >
                {submitQueryMutation.isPending ? (
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white" />
                ) : (
                  <Send className="w-4 h-4" />
                )}
              </Button>
            </div>

            <div className="flex flex-wrap gap-2">
              <span className="text-sm text-github-gray">Try asking:</span>
              {suggestedQueries.map((suggested) => (
                <button
                  key={suggested}
                  type="button"
                  className="text-sm bg-github-bg text-github-blue px-3 py-1 rounded-full hover:bg-blue-50 transition-colors"
                  onClick={() => handleSuggestedQuery(suggested)}
                  data-testid={`button-suggested-${suggested.replace(/[^a-zA-Z0-9]/g, '-')}`}
                >
                  {suggested}
                </button>
              ))}
            </div>
          </form>
        </CardContent>
      </Card>

      {/* Query Results */}
      {submitQueryMutation.error && (
        <Alert variant="destructive">
          <AlertDescription>
            {submitQueryMutation.error.message}
          </AlertDescription>
        </Alert>
      )}

      {currentResult && (
        <Card className="shadow-sm">
          <CardContent className="p-6">
            <div className="flex items-center space-x-2 mb-4">
              <Search className="text-github-blue w-5 h-5" />
              <h4 className="font-medium text-gray-900" data-testid="text-current-question">
                "{currentResult.question}"
              </h4>
              {currentResult.confidence && (
                <Badge variant="outline" className="ml-auto">
                  {Math.round(currentResult.confidence * 100)}% confidence
                </Badge>
              )}
            </div>

            <div className="prose max-w-none">
              <Alert className="border-github-blue bg-blue-50">
                <Info className="w-4 h-4" />
                <AlertDescription>
                  <div className="font-medium text-gray-900 mb-2">AI Analysis Result</div>
                  <div 
                    className="text-github-gray text-sm whitespace-pre-wrap"
                    data-testid="text-query-answer"
                    dangerouslySetInnerHTML={{ __html: currentResult.answer.replace(/\n/g, '<br>') }}
                  />
                </AlertDescription>
              </Alert>

              {currentResult.relatedCommits.length > 0 && (
                <div className="mt-6">
                  <h5 className="font-medium text-gray-900 mb-3">Related Commits</h5>
                  <div className="space-y-2">
                    {currentResult.relatedCommits.slice(0, 3).map((commitSha) => (
                      <div
                        key={commitSha}
                        className="bg-gray-50 rounded p-3 font-mono text-sm"
                        data-testid={`commit-${commitSha}`}
                      >
                        <span className="font-medium">{commitSha}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              <div className="mt-6 pt-4 border-t border-gray-200">
                <div className="text-xs text-github-gray">
                  <Info className="w-4 h-4 inline mr-1" />
                  Analysis based on repository history and commit patterns
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/repository-input.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertRepositorySchema, type InsertRepository } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { GitBranch, Search, AlertCircle } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";

interface RepositoryInputProps {
  onSubmit: (data: InsertRepository) => void;
  isLoading?: boolean;
  error?: string;
}

const formSchema = insertRepositorySchema.extend({
  cloneProtocol: insertRepositorySchema.shape.cloneProtocol.default("https"),
  defaultRef: insertRepositorySchema.shape.defaultRef.default("refs/heads/main"),
});

export default function RepositoryInput({ onSubmit, isLoading, error }: RepositoryInputProps) {
  const form = useForm<InsertRepository>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      url: "",
      name: "",
      description: "",
      defaultRef: "refs/heads/main",
      cloneProtocol: "https",
    },
  });

  const handleSubmit = (data: InsertRepository) => {
    // Extract repository name from URL if not provided
    if (!data.name && data.url) {
      const match = data.url.match(/\/([^\/]+?)(?:\.git)?$/);
      if (match) {
        data.name = match[1];
      }
    }
    onSubmit(data);
  };

  return (
    <div className="max-w-2xl mx-auto">
      <Card className="glass-card backdrop-blur-xl border-white/20 shadow-2xl">
        <CardContent className="p-8">
          <div className="text-center mb-8">
            <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg floating-animation">
              <Search className="text-white w-8 h-8" />
            </div>
            <h2 className="text-2xl font-bold gradient-text mb-2">Begin Analysis</h2>
            <p className="text-slate-600">Enter your repository URL to start exploring its evolution</p>
          </div>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            <div>
              <Label htmlFor="url" className="text-sm font-medium text-slate-700 mb-3 block flex items-center">
                <div className="w-2 h-2 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full mr-2"></div>
                Repository URL
              </Label>
              <div className="relative">
                <Input
                  id="url"
                  type="url"
                  placeholder="https://github.com/username/repository.git"
                  className="pl-12 pr-4 py-3 text-lg border-white/30 bg-white/50 backdrop-blur-sm rounded-xl focus:ring-2 focus:ring-blue-500/30 focus:border-blue-500/50 transition-all duration-300 hover:bg-white/70"
                  data-testid="input-repository-url"
                  {...form.register("url")}
                />
                <GitBranch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-500 w-5 h-5" />
              </div>
              {form.formState.errors.url && (
                <p className="text-sm text-red-500 mt-2 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-2" />
                  {form.formState.errors.url.message}
                </p>
              )}
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="name" className="text-sm font-medium text-slate-700 mb-3 block flex items-center">
                  <div className="w-2 h-2 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full mr-2"></div>
                  Repository Name (Optional)
                </Label>
                <Input
                  id="name"
                  placeholder="Auto-extracted from URL"
                  className="border-white/30 bg-white/50 backdrop-blur-sm rounded-xl focus:ring-2 focus:ring-green-500/30 focus:border-green-500/50 transition-all duration-300 hover:bg-white/70"
                  data-testid="input-repository-name"
                  {...form.register("name")}
                />
              </div>

              <div>
                <Label htmlFor="description" className="text-sm font-medium text-slate-700 mb-3 block flex items-center">
                  <div className="w-2 h-2 bg-gradient-to-r from-orange-500 to-red-500 rounded-full mr-2"></div>
                  Description (Optional)
                </Label>
                <Input
                  id="description"
                  placeholder="Brief description"
                  className="border-white/30 bg-white/50 backdrop-blur-sm rounded-xl focus:ring-2 focus:ring-orange-500/30 focus:border-orange-500/50 transition-all duration-300 hover:bg-white/70"
                  data-testid="input-repository-description"
                  {...form.register("description")}
                />
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="branch" className="text-sm font-medium text-slate-700 mb-3 block flex items-center">
                  <div className="w-2 h-2 bg-gradient-to-r from-purple-500 to-indigo-600 rounded-full mr-2"></div>
                  Branch (Optional)
                </Label>
                <Input
                  id="branch"
                  placeholder="main"
                  className="border-white/30 bg-white/50 backdrop-blur-sm rounded-xl focus:ring-2 focus:ring-purple-500/30 focus:border-purple-500/50 transition-all duration-300 hover:bg-white/70"
                  data-testid="input-branch"
                  value={form.watch("defaultRef")?.replace("refs/heads/", "") || ""}
                  onChange={(e) => form.setValue("defaultRef", `refs/heads/${e.target.value || "main"}`)}
                />
              </div>
              <div>
                <Label htmlFor="protocol" className="text-sm font-medium text-slate-700 mb-3 block flex items-center">
                  <div className="w-2 h-2 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full mr-2"></div>
                  Protocol
                </Label>
                <Select
                  value={form.watch("cloneProtocol")}
                  onValueChange={(value) => form.setValue("cloneProtocol", value as "https" | "ssh")}
                >
                  <SelectTrigger className="border-white/30 bg-white/50 backdrop-blur-sm rounded-xl focus:ring-2 focus:ring-cyan-500/30 focus:border-cyan-500/50 transition-all duration-300 hover:bg-white/70" data-testid="select-protocol">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-white/95 backdrop-blur-sm border-white/30 rounded-xl">
                    <SelectItem value="https">HTTPS</SelectItem>
                    <SelectItem value="ssh">SSH</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            {error && (
              <Alert className="border-red-200/50 bg-red-50/50 backdrop-blur-sm rounded-xl">
                <AlertCircle className="h-5 w-5 text-red-500" />
                <AlertDescription className="text-red-700 font-medium">{error}</AlertDescription>
              </Alert>
            )}

            <Button 
              type="submit" 
              disabled={isLoading} 
              className="w-full py-4 text-lg font-semibold bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 transition-all duration-300 rounded-xl shadow-lg hover:shadow-xl transform hover:scale-[1.02] disabled:hover:scale-100"
              data-testid="button-start-analysis"
            >
              {isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin mr-3"></div>
                  Starting Analysis...
                </div>
              ) : (
                <div className="flex items-center justify-center">
                  <Search className="w-5 h-5 mr-3" />
                  Start Analysis
                </div>
              )}
            </Button>
          </form>

          <div className="mt-8 text-center">
            <div className="flex items-center justify-center space-x-2 text-sm text-slate-500">
              <div className="w-2 h-2 bg-gradient-to-r from-blue-400 to-purple-500 rounded-full animate-pulse"></div>
              <p>Analysis typically takes 2-10 minutes depending on repository size</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/timeline-visualiza.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ArrowRight, ArrowUp } from "lucide-react";
import type { ChangeEvent } from "@shared/schema";

interface TimelineVisualizationProps {
  events: ChangeEvent[];
}

const getCategoryColor = (category: string) => {
  switch (category) {
    case "new_feature":
      return "bg-green-500";
    case "enhancement":
      return "bg-blue-500";
    case "bug_fix":
      return "bg-red-500";
    case "refactoring":
      return "bg-gray-500";
    case "optimization":
      return "bg-purple-500";
    default:
      return "bg-gray-500";
  }
};

const getCategoryBadgeStyle = (category: string) => {
  switch (category) {
    case "new_feature":
      return "bg-green-500 text-white border-green-600"; // green
    case "enhancement":
      return "bg-blue-500 text-white border-blue-600"; // blue 
    case "bug_fix":
      return "bg-red-500 text-white border-red-600"; // red
    case "refactoring":
      return "bg-gray-500 text-white border-gray-600"; // neutral
    case "optimization":
      return "bg-purple-500 text-white border-purple-600";
    default:
      return "bg-gray-500 text-white border-gray-600";
  }
};

const formatCategory = (category: string) => {
  return category
    .split("_")
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
    .join(" ");
};

export default function TimelineVisualization({
  events,
}: TimelineVisualizationProps) {
  const [showAll, setShowAll] = useState(false);

  // Sort events by timestamp (most recent first)
  const allSortedEvents = [...events].sort(
    (a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime(),
  );

  // Show only recent 10 events unless user wants to see all
  const sortedEvents = showAll ? allSortedEvents : allSortedEvents.slice(0, 10);

  if (sortedEvents.length === 0) {
    return (
      <Card className="enhanced-card">
        <CardContent className="p-10">
          <h3 className="text-2xl font-bold gradient-text mb-8 flex items-center">
            <div className="w-3 h-8 bg-gradient-to-b from-blue-500 to-purple-600 rounded-full mr-4"></div>
            Evolution Timeline
          </h3>
          <div className="text-center py-12">
            <div className="w-24 h-24 bg-gradient-to-br from-blue-100 to-purple-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <span className="text-4xl">â³</span>
            </div>
            <p className="text-gray-600 text-lg font-medium">
              No change events have been identified yet. Analysis may still be
              in progress.
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="glass-card">
      <CardContent className="p-10">
        <h3 className="text-2xl font-bold gradient-text mb-8 flex items-center">
          <div className="w-3 h-8 bg-gradient-to-b from-blue-500 to-purple-600 rounded-full mr-4"></div>
          Evolution Timeline
        </h3>

        <div className="relative">
          {/* Enhanced Timeline axis with gradient */}
          <div className="absolute left-10 top-0 bottom-0 w-1 bg-gradient-to-b from-blue-400 via-purple-500 to-indigo-600 rounded-full opacity-70 shadow-sm" />

          <div className="space-y-10">
            {sortedEvents.map((event, index) => (
              <div
                key={event.id}
                className="relative flex items-start space-x-8 group hover:bg-gradient-to-r hover:from-blue-50/30 hover:to-purple-50/30 rounded-2xl p-6 -m-6 transition-all duration-300 hover:shadow-lg"
                data-testid={`timeline-event-${index}`}
              >
                <div className="flex-shrink-0 relative">
                  <div
                    className={`w-6 h-6 ${getCategoryColor(event.category)} rounded-full border-4 border-white shadow-xl group-hover:scale-125 transition-all duration-300`}
                  />
                  <div
                    className={`absolute inset-0 w-6 h-6 ${getCategoryColor(event.category)} rounded-full animate-ping opacity-30`}
                  ></div>
                  <div className="absolute inset-0 w-6 h-6 bg-white rounded-full blur-sm opacity-50"></div>
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center space-x-3 mb-3">
                    <span
                      className="text-lg font-bold text-gray-900"
                      data-testid={`event-title-${index}`}
                    >
                      {event.title}
                    </span>
                    <div className={`inline-flex items-center rounded-full px-3 py-1 text-xs font-semibold ${getCategoryBadgeStyle(event.category)}`}>
                      {formatCategory(event.category)}
                    </div>
                  </div>
                  <p
                    className="text-gray-700 mb-4 leading-relaxed"
                    data-testid={`event-description-${index}`}
                  >
                    {event.description}
                  </p>
                  <div className="flex items-center space-x-4 text-sm text-gray-500">
                    <div className="flex items-center space-x-2 bg-gray-100 px-3 py-1 rounded-full">
                      <span className="w-2 h-2 bg-blue-400 rounded-full"></span>
                      <span
                        className="font-medium"
                        data-testid={`event-timestamp-${index}`}
                      >
                        {new Date(event.timestamp).toLocaleDateString()}
                      </span>
                    </div>
                    {event.commitShas.length > 0 && (
                      <div className="flex items-center space-x-2 bg-green-100 px-3 py-1 rounded-full">
                        <span className="w-2 h-2 bg-green-400 rounded-full"></span>
                        <span
                          className="font-medium"
                          data-testid={`event-commits-${index}`}
                        >
                          {event.commitShas.length} commit
                          {event.commitShas.length > 1 ? "s" : ""}
                        </span>
                      </div>
                    )}
                    {event.filesAffected.length > 0 && (
                      <div className="flex items-center space-x-2 bg-purple-100 px-3 py-1 rounded-full">
                        <span className="w-2 h-2 bg-purple-400 rounded-full"></span>
                        <span
                          className="font-medium"
                          data-testid={`event-files-${index}`}
                        >
                          {event.filesAffected.length} file
                          {event.filesAffected.length > 1 ? "s" : ""} affected
                        </span>
                      </div>
                    )}
                  </div>
                  {event.rationale && (
                    <div className="mt-4 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl border border-blue-200">
                      <strong className="text-gray-800 font-semibold">
                        Rationale:
                      </strong>
                      <span className="text-gray-700 ml-2">
                        {event.rationale}
                      </span>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>

        {events.length > 10 && (
          <div className="mt-10 text-center">
            <Button
              variant="outline"
              size="lg"
              onClick={() => setShowAll(!showAll)}
              className="bg-white/80 border-slate-300 hover:bg-white hover:shadow-xl transition-all duration-300 hover:scale-105 backdrop-blur-sm px-8 py-3"
              data-testid="button-view-complete-timeline"
            >
              {showAll ? (
                <>
                  Show Recent Only
                  <ArrowUp className="w-5 h-5 ml-2" />
                </>
              ) : (
                <>
                  View Complete Timeline ({events.length} total)
                  <ArrowRight className="w-5 h-5 ml-2" />
                </>
              )}
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

